const primaryColor = "#ffffff";

const lightTheme = {
  colors: {
    header: primaryColor,
    body: primaryColor,
    footer: primaryColor,
    text: "black",
  },
};

const darkTheme = {
  colors: {
    header: "#000000",
    body: "#000000",
    footer: "#000000",
    text: "#ffffff",
  },
};

const Themes = {
  LightTheme: lightTheme,
  DarkTheme: darkTheme,
};
export default Themes;
